value.string:
from Products.ERP5Type.Message import translateString

 if listbox is None:
 listbox = []

 portal = context.getPortalObject()
 message_list = []

 for parameters in listbox:
 new_sr_path = parameters['workflow_action']

 if new_sr_path is not None and new_sr_path != '':
 new_sr_document = portal.restrictedTraverse(new_sr_path)
 new_sr_url = new_sr_document.getUrlString()
 old_sr_document = portal.restrictedTraverse(parameters['listbox_key'])

 comp_reference_list = parameters['computer_filter']
 if comp_reference_list != []:
 for comp_reference in comp_reference_list:
 comp = portal.portal_catalog.getResultValue(portal_type = 'Computer', reference = comp_reference)
 if new_sr_url not in portal.portal_catalog.getResultValue(portal_type='Software Installation', default_aggregate_related_value=comp.getRelativeUrl()):
 message_list.append("Software Release %s not installed on %s." % (new_sr_document.getTitle(), comp_reference))
 else:
 message_list.append("No computers selected for upgrade from %s to %s" % (old_sr_document.getUrlString(), new_sr_url))

 tag = 'software_release_change_from_%s_to_%s' % (old_sr_document.getReference(), new_sr_document.getReference())

 old_sr_document.activate(tag=tag).SoftwareRelease_requestInstanceUpgrade(new_sr_url, comp_reference_list, tag)

 # activate something on the module after everything, so that user can know that something is happening in the background
 context.activate(after_tag=tag).getTitle()

 # reset selection checked uids
 context.portal_selections.setSelectionCheckedUidsFor(selection_name, [])

 portal_status_message = "Upgrade of instances in progress.\r\n"

 if message_list != []:
 portal_status_message += "\r\n".join(message_list)

 return context.Base_redirect(form_id,
 keep_items=dict(portal_status_message=translateString(portal_status_message)))
